"use client"

import type React from "react"
import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { skillsAPI } from "../../services/api"
import type { Skill, Category } from "../../types"
import { toast } from "react-toastify"
import { MagnifyingGlassIcon, StarIcon, ClockIcon, UserIcon } from "@heroicons/react/24/outline"

const Skills: React.FC = () => {
  const [skills, setSkills] = useState<Skill[]>([])
  const [categories, setCategories] = useState<Category[]>([])
  const [loading, setLoading] = useState(true)
  const [searchTerm, setSearchTerm] = useState("")
  const [selectedCategory, setSelectedCategory] = useState("")
  const [difficultyFilter, setDifficultyFilter] = useState("")
  const [sortBy, setSortBy] = useState("created_at")

  useEffect(() => {
    fetchSkills()
    fetchCategories()
  }, [searchTerm, selectedCategory, difficultyFilter, sortBy])

  const fetchSkills = async () => {
    try {
      setLoading(true)
      const response = await skillsAPI.getSkills({
        search: searchTerm,
        category: selectedCategory,
        difficulty: difficultyFilter,
      })
      // Fix: response is already { results: Skill[]; count: number }
      setSkills(response.results)
    } catch (error) {
      toast.error("Failed to fetch skills")
    } finally {
      setLoading(false)
    }
  }

  const fetchCategories = async () => {
    try {
      const response = await skillsAPI.getCategories()
      // Fix: response is already { results: Category[] }
      setCategories(response.results)
    } catch (error) {
      console.error("Failed to fetch categories")
    }
  }

  const getDifficultyColor = (difficulty: string) => {
    switch (difficulty) {
      case "beginner":
        return "bg-green-100 text-green-800"
      case "intermediate":
        return "bg-yellow-100 text-yellow-800"
      case "advanced":
        return "bg-red-100 text-red-800"
      default:
        return "bg-gray-100 text-gray-800"
    }
  }

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">Browse Skills</h1>
        <p className="text-gray-600">Discover and learn new skills from our community of experts</p>
      </div>

      {/* Filters */}
      <div className="bg-white rounded-lg shadow-md p-6 mb-8">
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {/* Search */}
          <div className="relative">
            <MagnifyingGlassIcon className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-gray-400" />
            <input
              type="text"
              placeholder="Search skills..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
            />
          </div>

          {/* Category Filter */}
          <select
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
          >
            <option value="">All Categories</option>
            {categories.map((category) => (
              <option key={category.id} value={category.name}>
                {category.name}
              </option>
            ))}
          </select>

          {/* Difficulty Filter */}
          <select
            value={difficultyFilter}
            onChange={(e) => setDifficultyFilter(e.target.value)}
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
          >
            <option value="">All Levels</option>
            <option value="beginner">Beginner</option>
            <option value="intermediate">Intermediate</option>
            <option value="advanced">Advanced</option>
          </select>

          {/* Sort */}
          <select 
            value={sortBy} 
            onChange={(e) => setSortBy(e.target.value)} 
            className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500"
          >
            <option value="created_at">Newest First</option>
            <option value="-created_at">Oldest First</option>
            <option value="rating">Highest Rated</option>
            <option value="points_required">Lowest Points</option>
            <option value="-points_required">Highest Points</option>
          </select>
        </div>
      </div>

      {/* Skills Grid */}
      {loading ? (
        <div className="flex justify-center items-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {skills.map((skill) => (
            <Link
              key={skill.id}
              to={`/skills/${skill.id}`}
              className="bg-white rounded-lg shadow-md hover:shadow-lg transition-shadow duration-200 overflow-hidden"
            >
              <div className="p-6">
                <div className="flex justify-between items-start mb-4">
                  <h3 className="text-lg font-semibold text-gray-900 line-clamp-2">{skill.title}</h3>
                  <span
                    className={`px-2 py-1 rounded-full text-xs font-medium ${getDifficultyColor(skill.difficulty_level)}`}
                  >
                    {skill.difficulty_level}
                  </span>
                </div>

                <p className="text-gray-600 text-sm mb-4 line-clamp-3">{skill.description}</p>

                <div className="flex items-center justify-between text-sm text-gray-500 mb-4">
                  <div className="flex items-center">
                    <UserIcon className="h-4 w-4 mr-1" />
                    {skill.instructor.first_name} {skill.instructor.last_name}
                  </div>
                  <div className="flex items-center">
                    <ClockIcon className="h-4 w-4 mr-1" />
                    {skill.duration_hours}h
                  </div>
                </div>

                <div className="flex items-center justify-between">
                  <div className="flex items-center">
                    <StarIcon className="h-4 w-4 text-yellow-400 mr-1" />
                    <span className="text-sm font-medium">{skill.rating.toFixed(1)}</span>
                    <span className="text-sm text-gray-500 ml-1">({skill.reviews_count})</span>
                  </div>
                  <div className="text-primary-600 font-semibold">{skill.points_required} points</div>
                </div>

                <div className="mt-4">
                  <span className="inline-block bg-gray-100 text-gray-800 text-xs px-2 py-1 rounded">
                    {skill.category}
                  </span>
                </div>
              </div>
            </Link>
          ))}
        </div>
      )}

      {skills.length === 0 && !loading && (
        <div className="text-center py-12">
          <div className="text-gray-500 text-lg mb-4">No skills found</div>
          <p className="text-gray-400">Try adjusting your search criteria</p>
        </div>
      )}
    </div>
  )
}

export default Skills
