"use client"

import type React from "react"
import { useState, useEffect, useCallback } from "react"
import { useParams, useNavigate } from "react-router-dom"
import { useAuth } from "../../contexts/AuthContext"
import { apiService } from "../../services/api"
import type { Skill } from "../../types"
import { toast } from "react-toastify"

const SkillDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>()
  const { user } = useAuth()
  const navigate = useNavigate()
  const [skill, setSkill] = useState<Skill | null>(null)
  const [loading, setLoading] = useState(true)
  const [ordering, setOrdering] = useState(false)

  const fetchSkill = useCallback(async () => {
    if (!id) return

    try {
      const data = await apiService.getSkill(Number.parseInt(id))
      setSkill(data)
    } catch (error) {
      console.error("Error fetching skill:", error)
      toast.error("حدث خطأ في تحميل تفاصيل المهارة")
      navigate("/skills")
    } finally {
      setLoading(false)
    }
  }, [id, navigate])

  useEffect(() => {
    fetchSkill()
  }, [fetchSkill])

  const handleOrder = async () => {
    if (!skill || !user) return

    if (user.points < skill.price) {
      toast.error("رصيدك من النقاط غير كافي لطلب هذه المهارة")
      return
    }

    setOrdering(true)
    try {
      await apiService.createOrder({
        skill: skill.id,
        message: "طلب جديد للمهارة",
      })
      toast.success("تم إرسال طلبك بنجاح!")
      navigate("/orders")
    } catch (error: any) {
      toast.error(error.response?.data?.detail || "حدث خطأ في إرسال الطلب")
    } finally {
      setOrdering(false)
    }
  }

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-blue-600 mx-auto"></div>
          <p className="mt-4 text-gray-600">جاري تحميل تفاصيل المهارة...</p>
        </div>
      </div>
    )
  }

  if (!skill) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-gray-900">المهارة غير موجودة</h2>
          <p className="mt-2 text-gray-600">لم يتم العثور على المهارة المطلوبة</p>
          <button
            onClick={() => navigate("/skills")}
            className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700"
          >
            العودة إلى المهارات
          </button>
        </div>
      </div>
    )
  }

  const isOwner = user?.id === skill.provider

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-4xl mx-auto">
        <div className="bg-white shadow overflow-hidden sm:rounded-lg">
          <div className="px-4 py-5 sm:px-6">
            <h1 className="text-3xl font-bold text-gray-900">{skill.title}</h1>
            <p className="mt-1 max-w-2xl text-sm text-gray-500">بواسطة {skill.provider_name}</p>
          </div>
          <div className="border-t border-gray-200 px-4 py-5 sm:p-0">
            <dl className="sm:divide-y sm:divide-gray-200">
              <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                <dt className="text-sm font-medium text-gray-500">الوصف</dt>
                <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">{skill.description}</dd>
              </div>
              <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                <dt className="text-sm font-medium text-gray-500">الفئة</dt>
                <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">{skill.category}</dd>
              </div>
              <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                <dt className="text-sm font-medium text-gray-500">السعر</dt>
                <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">{skill.price} نقطة</dd>
              </div>
              <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                <dt className="text-sm font-medium text-gray-500">المدة المتوقعة</dt>
                <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">{skill.duration} ساعة</dd>
              </div>
              <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                <dt className="text-sm font-medium text-gray-500">المتطلبات</dt>
                <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                  {skill.requirements || "لا توجد متطلبات خاصة"}
                </dd>
              </div>
              <div className="py-4 sm:py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                <dt className="text-sm font-medium text-gray-500">تاريخ الإنشاء</dt>
                <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                  {new Date(skill.created_at).toLocaleDateString("ar-SA")}
                </dd>
              </div>
            </dl>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="mt-8 flex justify-center space-x-4">
          <button
            onClick={() => navigate("/skills")}
            className="inline-flex items-center px-4 py-2 border border-gray-300 text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50"
          >
            العودة إلى المهارات
          </button>

          {!isOwner && user && (
            <button
              onClick={handleOrder}
              disabled={ordering || user.points < skill.price}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:opacity-50"
            >
              {ordering ? "جاري الإرسال..." : user.points < skill.price ? "رصيد غير كافي" : "طلب المهارة"}
            </button>
          )}

          {isOwner && (
            <button
              onClick={() => navigate(`/skills/${skill.id}/edit`)}
              className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-green-600 hover:bg-green-700"
            >
              تعديل المهارة
            </button>
          )}
        </div>

        {/* User Points Info */}
        {user && !isOwner && (
          <div className="mt-4 text-center">
            <p className="text-sm text-gray-600">
              رصيدك الحالي: <span className="font-semibold">{user.points || 0} نقطة</span>
            </p>
            {user.points < skill.price && (
              <p className="text-sm text-red-600 mt-1">تحتاج إلى {skill.price - (user.points || 0)} نقطة إضافية</p>
            )}
          </div>
        )}
      </div>
    </div>
  )
}

export default SkillDetail
