"use client"

import type React from "react"
import { useState, useEffect } from "react"
import { useAuth } from "../../contexts/AuthContext"
import { pointsAPI } from "../../services/api"
import type { PointsPackage, Transaction } from "../../types"
import { toast } from "react-toastify"
import { TrophyIcon, CreditCardIcon, ClockIcon } from "@heroicons/react/24/outline"

const Points: React.FC = () => {
  const { user, fetchProfile } = useAuth()
  const [packages, setPackages] = useState<PointsPackage[]>([])
  const [transactions, setTransactions] = useState<Transaction[]>([])
  const [loading, setLoading] = useState(true)
  const [purchasing, setPurchasing] = useState<number | null>(null)

  useEffect(() => {
    fetchPointsData()
  }, [])

  const fetchPointsData = async () => {
    try {
      setLoading(true)
      const [packagesResponse, transactionsResponse] = await Promise.all([
        pointsAPI.getPackages(),
        pointsAPI.getTransactions(),
      ])
      setPackages(packagesResponse.results)
      setTransactions(transactionsResponse.results)
    } catch (error) {
      toast.error("Failed to fetch points data")
    } finally {
      setLoading(false)
    }
  }

  const handlePurchase = async (packageId: number) => {
    try {
      setPurchasing(packageId)
      const result = await pointsAPI.purchasePoints(packageId)

      // Update user points balance by refetching profile
      if (user) {
        // You'll need to add this method to AuthContext or call it directly
        window.location.reload() // Temporary solution
      }

      toast.success("Points purchased successfully!")
      fetchPointsData() // Refresh transactions
    } catch (error) {
      toast.error("Failed to purchase points")
    } finally {
      setPurchasing(null)
    }
  }

  const getTransactionTypeColor = (type: string) => {
    switch (type) {
      case "purchase":
        return "bg-green-100 text-green-800"
      case "earn":
        return "bg-blue-100 text-blue-800"
      case "spend":
        return "bg-red-100 text-red-800"
      default:
        return "bg-gray-100 text-gray-800"
    }
  }

  const getTransactionIcon = (type: string) => {
    switch (type) {
      case "purchase":
        return "+"
      case "earn":
        return "+"
      case "spend":
        return "-"
      default:
        return ""
    }
  }

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    )
  }

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-2">Points & Wallet</h1>
        <p className="text-gray-600">Manage your points and view transaction history</p>
      </div>

      {/* Current Balance */}
      <div className="bg-gradient-to-r from-primary-500 to-primary-600 rounded-lg p-6 mb-8 text-white">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-lg font-medium mb-2">Current Balance</h2>
            <div className="flex items-center">
              <TrophyIcon className="h-8 w-8 mr-3" />
              <span className="text-4xl font-bold">{user?.points_balance || 0}</span>
              <span className="text-xl ml-2">Points</span>
            </div>
          </div>
          <div className="text-right">
            <p className="text-primary-100 text-sm">Member since</p>
            <p className="text-white">{user?.date_joined ? new Date(user.date_joined).toLocaleDateString() : "N/A"}</p>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Points Packages */}
        <div className="lg:col-span-2">
          <h2 className="text-2xl font-bold text-gray-900 mb-6">Buy Points</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {packages.map((pkg) => (
              <div
                key={pkg.id}
                className={`bg-white rounded-lg shadow-md p-6 border-2 ${
                  pkg.is_popular ? "border-primary-500 relative" : "border-gray-200"
                }`}
              >
                {pkg.is_popular && (
                  <div className="absolute -top-3 left-1/2 transform -translate-x-1/2">
                    <span className="bg-primary-500 text-white px-3 py-1 rounded-full text-sm font-medium">
                      Most Popular
                    </span>
                  </div>
                )}

                <div className="text-center">
                  <h3 className="text-xl font-bold text-gray-900 mb-2">{pkg.name}</h3>
                  <div className="mb-4">
                    <span className="text-3xl font-bold text-primary-600">{pkg.points}</span>
                    <span className="text-gray-600 ml-1">Points</span>
                  </div>
                  <div className="mb-6">
                    <span className="text-2xl font-bold text-gray-900">${pkg.price}</span>
                    <span className="text-gray-600 ml-1">{pkg.currency}</span>
                  </div>
                  <button
                    onClick={() => handlePurchase(pkg.id)}
                    disabled={purchasing === pkg.id}
                    className={`w-full py-3 px-4 rounded-lg font-medium transition-colors ${
                      pkg.is_popular
                        ? "bg-primary-600 hover:bg-primary-700 text-white"
                        : "bg-gray-100 hover:bg-gray-200 text-gray-900"
                    } disabled:opacity-50 disabled:cursor-not-allowed`}
                  >
                    {purchasing === pkg.id ? (
                      <div className="flex items-center justify-center">
                        <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-current mr-2"></div>
                        Processing...
                      </div>
                    ) : (
                      <div className="flex items-center justify-center">
                        <CreditCardIcon className="h-5 w-5 mr-2" />
                        Buy Now
                      </div>
                    )}
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Transaction History */}
        <div>
          <h2 className="text-2xl font-bold text-gray-900 mb-6">Recent Transactions</h2>
          <div className="bg-white rounded-lg shadow-md">
            {transactions.length > 0 ? (
              <div className="divide-y divide-gray-200">
                {transactions.slice(0, 10).map((transaction) => (
                  <div key={transaction.id} className="p-4">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center">
                        <div
                          className={`px-2 py-1 rounded-full text-xs font-medium ${getTransactionTypeColor(transaction.transaction_type)}`}
                        >
                          {transaction.transaction_type}
                        </div>
                        <div className="ml-3">
                          <p className="text-sm font-medium text-gray-900">{transaction.description}</p>
                          <div className="flex items-center text-xs text-gray-500 mt-1">
                            <ClockIcon className="h-3 w-3 mr-1" />
                            {new Date(transaction.created_at).toLocaleDateString()}
                          </div>
                        </div>
                      </div>
                      <div
                        className={`text-sm font-bold ${
                          transaction.transaction_type === "spend" ? "text-red-600" : "text-green-600"
                        }`}
                      >
                        {getTransactionIcon(transaction.transaction_type)}
                        {transaction.points}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="p-8 text-center">
                <TrophyIcon className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-500">No transactions yet</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  )
}

export default Points
